var FBPX = require('./fbp');
var Renderer = require('./renderer/chix');

/**
 *
 * FBP parser for chix.
 *
 * @example Parse an fbp string
 *
 *   var parser = require('fbpx/chix')();
 *   parser.parse(fbp_string);
 *
 *
 * @returns {FBPParser}
 *
 */
module.exports = function(options) {

  'use strict';

  var parser = new FBPX();
  parser.addRenderer( new Renderer(options) );

  return parser;

};
