'use strict';

function Tracker() {

  this._track = {};

}

Tracker.prototype.track = function(chi, portIdentifier) {

  if (!chi) {
    return;
  }
  for (var key in chi) {
    if (chi.hasOwnProperty(key)) {
      if (!this._track[key]) {
        this._track[key] = {};
      }
      if (!this._track[key][chi[key]]) {
        this._track[key][chi[key]] = [];
      }
      this._track[key][chi[key]].push(portIdentifier);
    }
  }
};

Tracker.prototype.show = function(chi) {
  if (!chi) {
    return;
  }
  /*
  for (var key in chi) {
  }
  */

};

module.exports = new Tracker();
