'use strict';

var util = require('util');
var Scope = require('dyslexer').Scope;

/**
 *
 */
function IndexScope(lexer) {
  Scope.apply(this, [lexer]);
}

util.inherits(IndexScope, Scope);

IndexScope.prototype.onEnter = function() {
};

IndexScope.prototype.onLineStart = function() {
  this.tokensExpected = 1;
};

IndexScope.prototype.setup = function() {

  this.validate = {
  };

  // Used by the lexer to validate token combinations.
  this.structure = [
    ['TARGET_INDEX']
  ];

};

IndexScope.prototype.onToken = function() {

  this.lexer.present('TARGET_INDEX');

};

module.exports = IndexScope;
